css:
 .header {
            text-align: center;
            margin-bottom: 10px;
        }
    .table-container {
            max-height: 400px; 
            overflow-y: auto;
        }
    .logo {
            width: 120px;
            height: 120px;
        }
      .sign{
            padding: 40px 20px;
            text-align: center;
        }
      .signature-line {
            border-bottom: 1px solid black;
            margin-top: 30px;
        }
        .signature{
            display:flex;
            justify-content: space-between;
        }
    .item-table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom:20px;
            margin-top:20px;
        }
    .border{
        border-bottom: 1px solid #ddd;
        border-top: 1px solid #ddd;
    }

        .item-table th, .item-table td {
            padding: 8px;
        }
        .print-format th {
            color: black;
            border-bottom: 1px solid black;
            border-top: 1px solid black;
            background-color: #ADD8E6;
            font-size:11px;
        }
        .print-format td {
            font-size:10px;
        }
        .print-format td, .print-format th {
            padding: 5px !important;
        }
        .print-format {
            padding: 0.3in;
        }
